import Typed from "typed.js";

const heroLogic = () => {
  const whatAmIStrings = ["nait graduate", "coder", "dungeon master", "cat mom", "coffee addict"];
  const typedOptions = {
    strings: "strings",
    typeSpeed: 50,
    backSpeed: 50,
    loop: true,
  };
  const myTyped = new Typed(this.el, whatAmIStrings, typedOptions);
};

const awaitForElement = (selectedElement) => {
  return new Promise((resolve) => {
    if (document.querySelector(selectedElement)) {
      return resolve(document.querySelector(selectedElement));
    }
  });
};

export { heroLogic, awaitForElement };
